import React, { useState } from 'react';
import '../App.css';
import Logo from '../Image/Logo.PNG';
import rech from '../Image/rech.png';

const Navvbar1 = () => {
  const [searchValue, setSearchValue] = useState('');
  const [rechercheActive, setRechercheActive] = useState(false);
  const [favorisActive, setFavorisActive] = useState(false);

  const handleSearch = () => {
    if (searchValue.trim() !== '') {
      alert(`cherchan pour ${searchValue}`);
    } else {
      alert('entrez une recherche valide SVP');
    }
  };

  const handleRechercheClick = () => {
    setRechercheActive(true);
    setFavorisActive(false);
  };

  const handleFavorisClick = () => {
    setFavorisActive(true);
    setRechercheActive(false);
  };

  return (
    <div style={{ boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)', backgroundColor: '#ffff', width: '100%', display: 'flex', flexDirection: 'row', alignItems: 'center' }}>

      <img
        style={{ padding: '10px', height: '32px', paddingRight: '40px', paddingLeft: '40px' }}
        src={Logo}
        alt="Logo"
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
      />

      <img
        src={rech}
        alt="rech"
        style={{ cursor: 'pointer', height: '25px', zIndex: 1 }}
        onClick={handleSearch}
      />

      <input
        type="text"
        placeholder="Rechercher un Article..."
        style={{
          height: '5px',
          padding: '15px',
          marginLeft: '-35px',
          paddingLeft: '45px',
          backgroundColor: '#ffff',
          borderRadius: '17px',
          borderColor: '#0E00AF',
          width: '400px',
        }}
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
      />

      <button
        style={{
          marginLeft: '-90px',
          padding: '8px 5px 8px 5px',
          color: '#ffff',
          borderRadius: '10px',
          backgroundColor: '#3E5CFB',
          border: 'none',
          cursor: 'pointer',

        }}
        onClick={handleSearch}
      >
        Rechercher
      </button>

      <button
        style={{
          marginLeft: '250px',
          marginRight: '70px',
          padding: '10px',
          color: rechercheActive ? '#3E5CFB' : '#0E00AF',
          fontWeight: rechercheActive ? 'bold' : 'normal',
          border: 'none',
          backgroundColor: '#ffff',
          cursor: 'pointer',
          position: 'relative',
          borderBottom: rechercheActive ? '3px solid #3E5CFB' : 'none',
        }}
        onClick={handleRechercheClick}
      >
        Recherche
      </button>

      <button
        style={{
          marginRight: '70px',
          padding: '10px',
          color: favorisActive ? '#3E5CFB' : '#0E00AF',
          fontWeight: favorisActive ? 'bold' : 'normal',
          border: 'none',
          backgroundColor: '#ffff',
          cursor: 'pointer',
          position: 'relative',
          borderBottom: favorisActive ? '3px solid #3E5CFB ' : 'none',
        }}
        onClick={handleFavorisClick}
      >
        Favoris
      </button>

      <button
        style={{
          padding: '8px',
          backgroundColor: '#ffff',
          borderRadius: '5px',
          borderColor: '#0E00AF',
          width: '155px',
          boxShadow: '0px 2px 2px rgba(14, 0, 175, 0.7)',
          marginRight: '10px',
        }}
      >
        Se Deconnecter
      </button>

    </div>
  );
};

export default Navvbar1;
