import React, { useState } from 'react';
import pageRechBack from '../Image/pageRechBack.png';
import Navvbar from './Navvbar';
import Logo from '../Image/Logo.PNG';
import rech from '../Image/rech.png';
const PageRech = () => {
  const [searchValue, setSearchValue] = useState('');

  const handleSearch = () => {
    if (searchValue.trim() !== '') {
      alert(`cherchan pour ${searchValue}`);
    } else {
      alert('entrez une recherche valide SVP');
    }
  };

  return (
    <div style={{
      backgroundColor: '#E2EBF6',
      backgroundSize: 'contain',
      backgroundRepeat: 'no-repeat',
      backgroundPosition: 'left',
      minHeight: '100vh',
      backgroundImage: `url(${pageRechBack})`,
      fontSize :'18px',
    }}>
      <Navvbar />



 <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
        minHeight: '100vh', fontSize :'18px',}}> 
         <img
          src={rech}
          alt="rech"
          style={{ position: 'absolute', left: '417px', top: '395px', cursor: 'pointer', height: '31px', marginTop:'50px', }}
          onClick={handleSearch}
        />
        <input
          type="text"
          placeholder="Rechercher un Article..."
          style={{
            marginTop:'80px',
            height: '11px',
            padding: '18px',
            paddingLeft: '85px',
            backgroundColor: '#ffff',
            borderRadius: '15px',
            borderColor: '#0E00AF',
            width: '580px', fontSize :'18px',
          }}
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
        />

        <button
          style={{
            position: 'absolute',
            top: '392px',
            left: '965px',
            padding: '8px 5px 8px 5px',
            color: '#ffff',
            borderRadius: '10px',
            backgroundColor: '#3E5CFB',
            border: 'none',
            cursor: 'pointer',
            marginTop:'50px', fontSize :'18px',
          }}
          onClick={handleSearch}
        >
          Rechercher
        </button>
      {/* Other components, content, etc. */}
      </div>
    </div>

  );
};

export default PageRech;
