import React, { useState } from 'react';
import Login from '../Image/Login.PNG'
import Logo from '../Image/Logo.PNG';
import { Link } from 'react-router-dom'; 
import '../App.css';
const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = () => {
    // Logique de connexion
    console.log('Username:', username);
    console.log('Password:', password);
  };

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };


  return (
    <div className="container">

     <div className="left"> 
     <div className="full-screen-image">
     <img src={Login} alt=''  className="full-width" style={{ width: '900px', height: '950px', objectFit: 'cover' }}/>
     </div>
     </div>

        <div className="right">
        <img src={Logo} alt="Logo de l'application"   style={{

            padding: '40px', 
            borderRadius: '10px',
            position: 'absolute',
            top: '20%',
            left: '70%',
            transform: 'translate(-50%, -50%)',
            width: '650px'
          }}/>
        {/* <div style={{ backgroundColor: '#76B5FF', padding: '40px', borderRadius: '50px' }}> */}
        <div 
          style={{
            backgroundColor: '#76B5FF',
            padding: '40px',
            borderRadius: '50px',
            position: 'absolute',
            top: '60%',
            left: '70%',
            transform: 'translate(-50%, -50%)',
          }}

        >
      {/* <form style={{  flex: '1', padding: '0 20px' }}> */}
        <div className="form-group">
          <label>Nom d‚Äôutilisateur </label>
          <br />
          <br/>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input-field" />
        </div>
        <h1></h1>
        <h1></h1>
        <h1></h1>
        <div className="form-group">
          <label>Mot de passe</label>
          <br />
          <br/>
          <div className="password-input">
            <input
              type={showPassword ? 'text' : 'password'}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="input-field"  />
            <button
              type="button"
              onClick={handleTogglePassword}
              className="password-toggle-button"
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </button>
          </div>

        </div>
        <div className="submit-group">
          <button type="button" onClick={handleLogin} style={{ whiteSpace: 'nowrap' }} className="login-button">
            Se onnecter
          </button>
        </div>
      {/* </form> */}
      </div>
      <a href="/CreateAccountPage" style={{

            padding: '40px', 
            borderRadius: '10px',
            position: 'absolute',
            top: '85%',
            left: '80%',
            transform: 'translate(-50%, -50%)',
            width: '650px'
          }}> <p>vous n‚Äôavez pas de compte? s‚Äôinscrire   </p></a>
      </div>
      {/* <Link to="/create-account">Create an Account</Link> */}


      </div>










  );
};

export default LoginPage;
