import React, { useState } from 'react';
import Navvbar1 from "./Navvbar1";

const Resultat = () => {
  const [articleTitle, setArticleTitle] = useState('');
  const [articleDate, setArticleDate] = useState('');
  const [buttonActive, setButtonActive] = useState(false);
  return (
    <>
      <Navvbar1 />

      <div style={{ backgroundColor: '#E1EBF6', minHeight: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <a href="#section1">Aller à la section 1</a>
        <div style={{
          maxWidth: '800px',
          width: '100%',
          backgroundColor: 'white',
          border: '2px solid #ccc',
          borderRadius: '10px',
          padding: '20px',
          margin: '0 auto',
          overflow: 'hidden', // ou 'auto'
          wordWrap: 'break-word' // ou overflowWrap: 'break-word'
        }}>
           <h1>L’art dans les 1980 {articleTitle}</h1>
           <h3 style={{ display: 'inline-block', marginRight: '10px' }}>Publié le :</h3>
           <p style={{ display: 'inline-block' }}> Janvier 2017 </p>
           <button type="button"  
        style={{
          width: '40px',
          height: '40px',
          fontSize: '16px',
          padding: '8px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          backgroundColor: buttonActive ? '#B0C5E8' : '#E1EBF6', // Changement de couleur lors du clic
          outline: 'none',
          cursor: 'pointer',
          color: '#0E00AF', // Couleur du texte
          display: 'inline-block',
        }} >
            Se connecter
          </button>
        </div>
      </div>
    </>
  );
};

export default Resultat;
