import React, { useState } from 'react';
import Login from '../Image/Login.PNG'
import Logo from '../Image/Logo.PNG';
import { Link } from 'react-router-dom';
const CreateAccountPage = () => { 
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleUserNameChange = (e) => {
    setUserName(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleFirstNameChange = (e) => {
    setFirstName(e.target.value);
  };

  const handleLastNameChange = (e) => {
    setLastName(e.target.value);
  };

  const handleBirthdateChange = (e) => {
    setBirthdate(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setPasswordError('Les mots de passe ne correspondent pas');
      return;
    }

    // Clear the password error if passwords match
    setPasswordError('');

    // TODO: Implement form submission logic here
    console.log('Form submitted');
    // You can add your form submission logic here, like sending data to an API, etc.
  };
  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };


  return (
    <div className="container">

     <div className="left"> 
     <div className="full-screen-image">
     <img src={Login} alt=''  className="full-width"/>
     </div>
     </div>
        <div className="right">
        <img src={Logo} alt="Logo de l'application"   style={{

            padding: '40px',
            borderRadius: '10px',
            position: 'absolute',
            top: '10%',
            left: '70%',
            transform: 'translate(-50%, -50%)',
            width: '450px'
          }}/>
        {/* <div style={{ backgroundColor: '#76B5FF', padding: '40px', borderRadius: '50px' }}> */}
        <div 
          style={{
            backgroundColor: '#76B5FF',
            padding: '30px',
            borderRadius: '50px',
            position: 'absolute',
            top: '55%',
            left: '70%',
            transform: 'translate(-50%, -50%)',
          }}

        >
      {/* <form style={{  flex: '1', padding: '0 20px' }}> */}
        <div className="form-group">
        <label htmlFor="username">Nom d‚Äôutilisateur</label>
        <br />
        <br />
          <input type="text" id="username" value={userName} onChange={handleUserNameChange} className="input-field" />
          <br /><br />

        </div>
        {/* -------------------------------------------------------------------------------------- */}
        {/* <h1></h1>
        <h1></h1>
        <h1></h1> */}
        <div className="form-group">
          <label htmlFor="password">Mot de passe</label>
          <br />
          <br/>
          <div className="password-input">
          <input  type={showPassword ? 'text' : 'password'} id="password" value={password} onChange={handlePasswordChange}  className="input-field" />

            <button
              type="button"
              onClick={handleTogglePassword}
              className="password-toggle-button"
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </button>

          </div>

        </div>
        {/* -------------------------------------------------------------------------------------- */}
        <div className="form-group">
        <br/>
        <label htmlFor="confirmPassword">Confirmer le mot de passe</label>
        <br /><br />

        <div className="password-input">
          <input
            type={showPassword ? 'text' : 'password'}
            id="confirmPassword"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            className="input-field"
          />

          {passwordError && <p style={{ color: 'red' }}>{passwordError}</p>}
          <button
              type="button"
              onClick={handleTogglePassword}
              className="password-toggle-button"
            >
              {showPassword ? 'üëÅÔ∏è' : 'üîí'}
            </button>

          </div>
</div>
{/* -------------------------------------------------------------------------------------- */}
<div className="form-group">
<br />
<label htmlFor="email">E-mail</label>
<br /><br />

          <input type="email" id="email" value={email} onChange={handleEmailChange}  className="input-field" />

          <br /><br />
</div>
{/* -------------------------------------------------------------------------------------- */}
<div className="form-group">
<label htmlFor="firstName">Nom</label>
<br /><br />

          <input type="text" id="firstName" value={firstName} onChange={handleFirstNameChange}  className="input-field" />
          <br /><br />
          </div>
 {/* -------------------------------------------------------------------------------------- */}
 <div className="form-group">
 <label htmlFor="lastName">Pr√©nom</label>
 <br /><br />

          <input type="text" id="lastName" value={lastName} onChange={handleLastNameChange} className="input-field" />
          <br /><br />
 </div>
{/* -------------------------------------------------------------------------------------- */}
<div className="form-group">
<label htmlFor="birthdate">Date de naissance</label>
<br /><br />

          <input type="date" id="birthdate" value={birthdate} onChange={handleBirthdateChange} className="input-field"/>
          <br /><br />
</div>



  {/* -------------------------------------------------------------------------------------- */}

        <div className="submit-group">
          <button type="button" style={{ display: 'inline-block', margin: 0, padding: '10px 20px' }}  onClick={handleSubmit} className="login-button">
          Creer un compte
          </button>
        </div>
      {/* </form> */}
      </div>
      </div>
      {/* <p>Vous n'avez pas de compte ? <Link to="/create-account">Cr√©er un compte</Link></p> */}
      </div>










  );
};

export default CreateAccountPage;
