// LoginPage.js
import React, { useState } from 'react';
import Login from '../Image/Login.PNG'
import '../App.css';
const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = () => {
    // Logique de connexion
    console.log('Username:', username);
    console.log('Password:', password);
  };

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };


  return (
    // <><div className="half-page" style={{ display: 'flex', width: 1000, height: 600 }}>
    //   <div className="image-section" style={{ flex: '1' }}>
    //     <img src={Login} alt="Description de ton image" className="image-style" />
    //   </div>
    // </div><>
        <div style={{ backgroundColor: '#76B5FF', padding: '40px', borderRadius: '50px' }}>


<div className="half-page" style={{ display: 'flex', width: '50%' }}>
<form style={{ flex: '1', padding: '0 20px' }}>
            <div className="form-group">
              <label>Nom d’utilisateur </label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="input-field" />
            </div>
            <h1></h1>
            <h1></h1>
            <h1></h1>
            <div className="form-group">
              <label>Mot de passe</label>
              <div className="password-input">
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="input-field" />
                <button
                  type="button"
                  onClick={handleTogglePassword}
                  className="password-toggle-button"
                >
                  {showPassword ? '👁️' : '🔒'}
                </button>
              </div>
              <button
                type="button"
                onClick={handleTogglePassword}
                className="password-toggle-button"
              >
                {showPassword ? '👁️' : '🔒'}
              </button>
            </div>
            <div className="submit-group">
              <button type="button" onClick={handleLogin} className="login-button">
                Se connecter
              </button>
            </div>
          </form>
</div>
<div className="image-section" style={{ width: '50%' }}>
          <img src={Login} alt="Description de ton image" className="image-style" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
        </div>
        </div>


  );
};

export default LoginPage;
